import React, { useEffect, useState } from 'react';
import axios from 'axios';

const DonationList = () => {
  const [donations, setDonations] = useState([]);

  useEffect(() => {
    const fetchDonations = async () => {
      try {
        // Include the Authorization header if needed
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/donations', {
          headers: {
            Authorization: `Bearer ${token}`, // If your endpoint requires authentication
          },
        });
        setDonations(response.data);
      } catch (error) {
        console.error('Error fetching donations:', error);
      }
    };

    fetchDonations();
  }, []);

  return (
    <div>
      <h2>Available Donations</h2>
      <ul>
        {donations.map((donation) => (
          <li key={donation._id}>
            <h3>{donation.itemName}</h3> {/* Display item name */}
            <p>Description: {donation.description}</p> {/* Display description */}
            <p>Category: {donation.category}</p> {/* Display category */}
            <p>Location: {donation.location}</p> {/* Display location */}
            <p>Status: {donation.status}</p> {/* Display status */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DonationList;
