// src/components/ChatPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Chat from './Chat'; // Import your Chat component
import './Chat.css';

const ChatPage = ({ currentUserId, notifications, setNotifications }) => {
  const { donorId } = useParams(); // Get the donorId from the URL
  const [chatHistory, setChatHistory] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch chat history with the selected donor
    const fetchChatHistory = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/chat/${currentUserId}/${donorId}`);
        setChatHistory(response.data);
      } catch (err) {
        setError('Error fetching chat history');
      }
    };

    fetchChatHistory();
  }, [donorId, currentUserId]);

  if (error) return <p>{error}</p>;

  return (
    <div className="chat-page">
      <h2>Chat with Donor</h2>
      {chatHistory.length === 0 ? (
        <p>No chats yet. Start the conversation!</p>
      ) : (
        <Chat donorId={donorId} doneeId={currentUserId} notifications={notifications} setNotifications={setNotifications} />
      )}
    </div>
  );
};

export default ChatPage;
