// Chat.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';
import axios from 'axios';

const Chat = ({ userId, donorId, chatHistory, setChatHistory }) => {
  const [newMessage, setNewMessage] = useState("");
  let socket;

  useEffect(() => {
    // Connect to WebSocket server
    socket = io('http://localhost:5000');
    socket.emit('joinChat', { donorId, doneeId: userId });

    // Listen for incoming messages
    socket.on('messageReceived', message => {
      setChatHistory(prev => [...prev, message]);
    });

    return () => {
      socket.disconnect();
    };
  }, [donorId, userId, setChatHistory]);

  const handleSendMessage = async () => {
    const messageData = { sender: userId, receiver: donorId, message: newMessage };
    try {
      // Save the message to the database
      await axios.post('/api/chats', messageData);
      // Emit the message through WebSocket
      socket.emit('sendMessage', messageData);
      setNewMessage('');
    } catch (error) {
      console.error("Failed to send message:", error);
    }
  };

  return (
    <div>
      <h3>Chat with Donor</h3>
      <div>
        {chatHistory && chatHistory.length > 0 ? (
          chatHistory.map((msg, index) => (
            <p key={index}>{msg.sender === userId ? "You" : "Donor"}: {msg.message}</p>
          ))
        ) : (
          <p>No messages yet</p>
        )}
      </div>
      <textarea
        value={newMessage}
        onChange={e => setNewMessage(e.target.value)}
        placeholder="Type a message"
      />
      <button onClick={handleSendMessage}>Send</button>
    </div>
  );
};

export default Chat;
