import React, { useState } from 'react';
import axios from 'axios';

const DonationForm = () => {
  const [description, setDescription] = useState('');
  const [itemCondition, setItemCondition] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/donations', { description, itemCondition });
      console.log(response.data);
      // Handle successful donation submission
    } catch (error) {
      console.error('Error submitting donation:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Donate an Item</h2>
      <input type="text" placeholder="Item Description" value={description} onChange={(e) => setDescription(e.target.value)} required />
      <input type="text" placeholder="Item Condition" value={itemCondition} onChange={(e) => setItemCondition(e.target.value)} required />
      <button type="submit">Submit Donation</button>
    </form>
  );
};

export default DonationForm;
