// src/components/DonationDetails.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

const DonationDetails = ({ currentUserId }) => {
  const { donationId } = useParams();
  const [donation, setDonation] = useState(null);
  const [initialMessage, setInitialMessage] = useState("");
  const [buttonLabel, setButtonLabel] = useState("Send Message");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDonationDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/donations/${donationId}`);
        setDonation(response.data);
        console.log('Donor ID:', response.data.donor._id);
        console.log('Current User ID:', currentUserId);
      } catch (error) {
        console.error("Error fetching donation data:", error);
        setError("Failed to load donation details.");
      }
    };
    fetchDonationDetails();
  }, [donationId, currentUserId]);

  const handleSendMessage = async () => {
    if (!initialMessage.trim()) return;

    const donorId = donation?.donor?._id;
    if (!currentUserId || !donorId) {
      setError("Unable to send message due to missing user information.");
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/chats', {
        sender: currentUserId,
        receiver: donorId,
        message: initialMessage,
        donationId: donation._id
      });

      if (response.status === 201) {
        setInitialMessage(response.data.message); // Set placeholder as the sent message
        setButtonLabel("Go to Chat"); // Change button text
      }
    } catch (error) {
      console.error("Error initiating chat:", error.response?.data || error.message);
      setError("Failed to send message. Please try again.");
    }
  };

  if (error) return <div>Error: {error}</div>;
  if (!donation) return <div>Loading donation details...</div>;

  const donor = donation.donor || {};

  return (
    <div>
      <h2>{donation.itemName}</h2>
      <p>{donation.description}</p>
      <p>Category: {donation.category}</p>
      <p>Location: {donation.location}</p>
      <p>Status: {donation.status}</p>
      
      <h3>Donor Information</h3>
      <p><strong>Name:</strong> {donor.name || 'Not provided'}</p>
      <p><strong>Email:</strong> {donor.email || 'Not provided'}</p>

      <textarea
        placeholder={initialMessage || "Type your message..."}
        value={initialMessage}
        onChange={(e) => setInitialMessage(e.target.value)}
      />
      <button onClick={() => buttonLabel === "Go to Chat" ? navigate(`/chat/${donor._id}`) : handleSendMessage()}>
        {buttonLabel}
      </button>
    </div>
  );
};

export default DonationDetails;
