// src/components/Notifications.js

import React, { useEffect, useState } from 'react';
import { apiGet } from '../utils/api';

const Notifications = ({ notifications }) => {
  const [initialNotifications, setInitialNotifications] = useState([]);
  const [allNotifications, setAllNotifications] = useState([]);

  useEffect(() => {
    const fetchNotifications = async () => {
      try {
        const response = await apiGet(`/api/notifications/user/${userId}`);
        console.log('Fetched notifications:', response.data);
        setInitialNotifications(response);
        setAllNotifications(response); // Initially load all notifications
      } catch (error) {
        console.error("Error fetching notifications:", error);
      }
    };

    fetchNotifications();
  }, [userId]);

  useEffect(() => {
    if (notifications.length > 0) {
      // Update the list of notifications when a new notification is received
      setAllNotifications((prevNotifications) => [
        ...prevNotifications,
        ...notifications
      ]);
    }
  }, [notifications]); // Listen for prop changes for real-time updates

  return (
    <div>
      <h3>Notifications</h3>
      <ul>
        {allNotifications.length === 0 ? (
          <p>No notifications yet!</p>
        ) : (
          allNotifications.map((note, index) => (
            <li key={index}>{`New message from ${note.sender}: ${note.content || note.message}`}</li>
          ))
        )}
      </ul>
    </div>
  );
};

export default Notifications;
