import React, { useState, useEffect, createContext } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Login from './components/Login';
import Register from './components/Register';
import DonationForm from './components/DonationForm';
import DonationList from './components/DonationList';
import Navbar from './components/Navbar';
import Home from './components/Home';
import Profile from './components/Profile';
import Notifications from './components/Notifications';
import DonationDetails from './components/DonationDetails';
import ChatPage from './components/ChatPage';
import ChatListPage from './components/ChatListPage';
import io from 'socket.io-client';

export const SocketContext = createContext();

const App = () => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [donationId, setDonationId] = useState(null);

  // Load user data from localStorage on initial load
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setUser(storedUser);
      setIsAuthenticated(true);
    }
  }, []);

  // Initialize socket only when user is authenticated
  const socket = user ? io('http://localhost:5000', { query: { userId: user.id } }) : null;

  useEffect(() => {
    if (!socket) return;

    socket.on('connect', () => {
      console.log("Socket connected with ID:", socket.id);
    });

    socket.on('disconnect', () => {
      console.log("Socket disconnected.");
    });

    socket.on('messageReceived', (messageData) => {
      setNotifications((prevNotifications) => [
        ...prevNotifications,
        { sender: messageData.sender, content: messageData.content },
      ]);
    });

    return () => {
      socket.disconnect();
      socket.off('messageReceived');
    };
  }, [socket]);

  const handleLogin = (userData) => {
    console.log('User logged in with data:', userData);
    setUser(userData);
    setIsAuthenticated(true);
    localStorage.setItem('user', JSON.stringify(userData)); // Save user data in localStorage
  };

  const handleLogout = () => {
    setUser(null);
    setIsAuthenticated(false);
    localStorage.removeItem('user'); // Clear user data from localStorage
  };

  return (
    <SocketContext.Provider value={socket}>
      <Router>
        <Navbar
          isAuthenticated={isAuthenticated}
          userType={user?.userType}
          onLogout={handleLogout}
          donorId={user?.id}
        />
        <ToastContainer />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/home" element={<Home />} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register />} />

          {/* Conditional Routes */}
          <Route
            path="/donate"
            element={
              isAuthenticated && user?.userType === 'donor' ? <DonationForm /> : <Navigate to="/login" />
            }
          />
          <Route
            path="/donations"
            element={
              isAuthenticated && user?.userType === 'donee' ? <DonationList /> : <Navigate to="/login" />
            }
          />
          <Route
            path="/profile"
            element={isAuthenticated ? <Profile user={user} /> : <Navigate to="/login" />}
          />
        <Route
            path="/notifications"
            element={isAuthenticated ? <Notifications userId={user?.id} notifications={notifications} /> : <Navigate to="/login" />
          }
          />
          <Route
            path="/donation/:donationId"
            element={
              isAuthenticated ? (
                <DonationDetails currentUserId={user?.id} setDonationId={setDonationId} />
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route
            path="/chats"
            element={
              isAuthenticated ? (
                <ChatListPage currentUserId={user?.id} />
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route
            path="/chat/:donorId/:donationId"
            element={
              isAuthenticated ? (
                <ChatPage
                  currentUserId={user?.id}
                  notifications={notifications}
                  setNotifications={setNotifications}
                />
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route
            path="/chat/:donorId"
            element={
              isAuthenticated ? (
                <ChatPage
                  currentUserId={user?.id}
                  notifications={notifications}
                  setNotifications={setNotifications}
                />
              ) : (
                <Navigate to="/login" />
              )
            }
          />
        </Routes>
      </Router>
    </SocketContext.Provider>
  );
};

export default App;
