// src/components/DonationDetails.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import Chat from './Chat';

const DonationDetails = ({ currentUserId }) => {
  const { donationId } = useParams(); // Extract donationId from URL params
  const [donation, setDonation] = useState(null);
  const [initialMessage, setInitialMessage] = useState("");
  const [showChat, setShowChat] = useState(false);
  const [error, setError] = useState(""); // State to hold error messages
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDonationDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/donations/${donationId}`);
        setDonation(response.data);
        
        // Log the donor ID and current user ID
        console.log('Donor ID:', response.data.donor._id); // Log the donor ID from the response
        console.log('Current User ID:', currentUserId); // Log the current user's ID
      } catch (error) {
        console.error("Error fetching donation data:", error);
        setError("Failed to load donation details.");
      }
    };

    fetchDonationDetails();
  }, [donationId, currentUserId]); // Include currentUserId in dependencies

  const handleSendMessage = async () => {
    if (!initialMessage.trim()) return;

    const donorId = donation?.donor?._id;

    if (!currentUserId || !donorId) { 
      console.error("Missing currentUserId or donation.donor._id");
      setError("Unable to send message due to missing user information.");
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/chats', {
        sender: currentUserId,
        receiver: donorId,  // Use donor ID from API response
        message: initialMessage,
        donationId: donation._id
      });

      if (response.status === 201) {
        setInitialMessage(""); // Clear message input
        //setShowChat(true); // Show chat component after sending the message
      }
    } catch (error) {
      console.error("Error initiating chat:", error.response?.data || error.message);
      setError("Failed to send message. Please try again.");
    }
  };

  if (error) return <div>Error: {error}</div>; // Display error if any
  if (!donation) return <div>Loading donation details...</div>; // Loading state

  // Extract donor information
  const donor = donation.donor || {}; // Access the donor from the donation object

  return (
    <div>
      <h2>{donation.itemName}</h2>
      <p>{donation.description}</p>
      <p>Category: {donation.category}</p>
      <p>Location: {donation.location}</p>
      <p>Status: {donation.status}</p>
      
      <h3>Donor Information</h3>
      <p><strong>Name:</strong> {donor.name || 'Not provided'}</p>
      <p><strong>Email:</strong> {donor.email || 'Not provided'}</p> {/* Ensure you have an email field available */}

      <textarea
        placeholder="Type your message..."
        value={initialMessage}
        onChange={(e) => setInitialMessage(e.target.value)}
      />
      <button onClick={handleSendMessage}>Send Message</button>

      {showChat && (
        <Chat 
          userId={currentUserId} 
          donorId={donor._id} // Use the donorId from the API response
          donationId={donation._id} 
          chatHistory={[]} 
          setChatHistory={() => {}} 
        />
      )}
    </div>
  );
};

export default DonationDetails;
