// src/components/DonationDetails.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const DonationDetails = ({ currentUserId }) => {
  const [donation, setDonation] = useState(null); // State to hold donation data
  const [initialMessage, setInitialMessage] = useState("");
  const navigate = useNavigate();
  const { donationId } = useParams(); // Assuming donation ID is in the URL
  
  console.log("Donation ID:", donationId); // Check if donationId is extracted correctly

  // Fetch donation data when the component mounts
  useEffect(() => {
    const fetchDonation = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/donations/${donationId}`);

        setDonation(response.data); // Set the donation data
      } catch (error) {
        console.error("Error fetching donation data:", error);
      }
    };

    fetchDonation();
  }, [donationId]);

  const handleSendMessage = async () => {
    if (!initialMessage.trim()) return;

    try {
      await axios.post('/api/chats', {
        sender: currentUserId,
        receiver: donation?.donorId, // Check if donation is defined
        message: initialMessage,
        donationId: donation?._id
      });

      setInitialMessage("");
      navigate(`/chat/${donation.donorId}/${donation._id}`);
    } catch (error) {
      console.error("Error initiating chat:", error);
    }
  };

  return (
    <div>
      {donation ? (
        <>
          <h2>{donation.itemName}</h2>
          <p>{donation.description}</p>
          <p>Category: {donation.category}</p>
          <p>Location: {donation.location}</p>
          <p>Status: {donation.status}</p>

          <textarea
            placeholder="Type your message..."
            value={initialMessage}
            onChange={(e) => setInitialMessage(e.target.value)}
          />
          <button onClick={handleSendMessage}>Send Message</button>
        </>
      ) : (
        <p>Loading donation details...</p>
      )}
    </div>
  );
};

export default DonationDetails;
