import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { apiGet } from '../utils/api';
import Chat from './Chat';
import './Chat.css';

const ChatPage = ({ currentUserId, notifications, setNotifications }) => {
  const { donorId, donationId } = useParams();
  const [chatHistory, setChatHistory] = useState([]);
  const [error, setError] = useState(null);
  const [isChatInitiated, setIsChatInitiated] = useState(false);
  const [itemName, setItemName] = useState(''); // For chat heading

  useEffect(() => {
    const checkIfChatExists = async () => {
      try {
        const response = await apiGet(`/api/chats/existence/${currentUserId}/${donorId}/${donationId}`);
        
        if (response?.exists) { // Confirm response is valid
          setIsChatInitiated(true);
          setItemName(response.itemName); // Set item name for heading

          const historyResponse = await apiGet(`/api/chats/${currentUserId}/${donorId}/${donationId}`);
          setChatHistory(historyResponse || []);
        } else {
          setIsChatInitiated(false);
          setChatHistory([]);
        }
      } catch (err) {
        console.error("Error fetching chat data:", err);
        setError("Unable to load chat history. Please try again later.");
      }
    };

    checkIfChatExists();
  }, [currentUserId, donorId, donationId]);

  return (
    <div className="chat-page">
      <h2>Chat about: {itemName}</h2> {/* Display item name as heading */}
      {error && <p className="error-message">{error}</p>}
      {isChatInitiated ? (
        <Chat 
          userId={currentUserId} 
          donorId={donorId} 
          donationId={donationId}
          chatHistory={chatHistory} 
          setChatHistory={setChatHistory} 
          notifications={notifications} 
          setNotifications={setNotifications} 
        />
      ) : (
        <p>No chat history found. Please initiate chat from the donation details page.</p>
      )}
    </div>
  );
};

export default ChatPage;
