import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { apiGet, apiPost } from '../utils/api';
import './ChatPage.css';

const ChatPage = ({ currentUserId }) => {
  const { donorId, donationId } = useParams();
  const [chatGroups, setChatGroups] = useState([]);
  const [selectedChatGroupId, setSelectedChatGroupId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch chat groups for the current user
    const fetchChatGroups = async () => {
      try {
        const response = await apiGet(`/api/chats/user/${currentUserId}`);
        setChatGroups(response);
      } catch (error) {
        console.error("Error fetching chat groups:", error);
      }
    };

    fetchChatGroups();
  }, [currentUserId]);

  // Fetch messages for the selected chat group
  useEffect(() => {
    const fetchMessages = async () => {
      if (!selectedChatGroupId) return;

      try {
        const response = await apiGet(`/api/messages/${selectedChatGroupId}`);
        setMessages(response);
      } catch (error) {
        console.error("Error fetching messages:", error);
      }
    };

    fetchMessages();
  }, [selectedChatGroupId]);

  const handleSendMessage = async () => {
    if (!newMessage.trim()) return;

    try {
      await apiPost('/api/messages', {
        chatGroupId: selectedChatGroupId,
        senderId: currentUserId,
        content: newMessage,
      });
      setNewMessage('');
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: currentUserId, content: newMessage },
      ]);
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  return (
    <div className="chat-page">
      <div className="chat-list">
        <h2>Chats</h2>
        {chatGroups.length > 0 ? (
          chatGroups.map((chatGroup) => (
            <div
              key={chatGroup._id}
              onClick={() => setSelectedChatGroupId(chatGroup._id)}
              className={selectedChatGroupId === chatGroup._id ? 'chat-item active' : 'chat-item'}
            >
              {chatGroup.groupName}
            </div>
          ))
        ) : (
          <p>No chats available.</p>
        )}
      </div>
      <div className="chat-messages">
        {selectedChatGroupId ? (
          <>
            <div className="messages">
              {messages.map((msg, index) => (
                <div
                  key={index}
                  className={`message ${msg.sender === currentUserId ? 'own' : ''}`}
                >
                  {msg.content}
                </div>
              ))}
            </div>
            <div className="message-input">
              <input
                type="text"
                placeholder="Type a message..."
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
              />
              <button onClick={handleSendMessage}>Send</button>
            </div>
          </>
        ) : (
          <p>Select a chat to start messaging</p>
        )}
      </div>
    </div>
  );
};

export default ChatPage;
