// src/components/ChatPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Chat from './Chat'; // Import your Chat component
import './Chat.css';

const ChatPage = ({ currentUserId, notifications, setNotifications }) => {
  const { donorId, donationId } = useParams(); // Get donorId and donationId from URL
  const [chatHistory, setChatHistory] = useState([]); // Initialize as an empty array
  const [error, setError] = useState(null);
  const [isChatInitiated, setIsChatInitiated] = useState(false);

  useEffect(() => {
    const checkIfChatExists = async () => {
      try {
        // Check if a chat exists for this donation between the current user (donee) and the donor
        const response = await axios.get(`/api/chats/existence/${currentUserId}/${donorId}/${donationId}`);
        setIsChatInitiated(response.data.exists);

        // Fetch chat history only if chat exists
        if (response.data.exists) {
          const historyResponse = await axios.get(`/api/chats/${currentUserId}/${donorId}/${donationId}`);
          setChatHistory(historyResponse.data || []);
        }
      } catch (err) {
        setError("Error fetching chat history");
      }
    };

    checkIfChatExists();
  }, [donorId, donationId, currentUserId]);

  if (error) return <p>{error}</p>;

  return (
    <div className="chat-page">
      <h2>Chat with Donor</h2>
      {isChatInitiated ? (
        <Chat 
          userId={currentUserId} 
          donorId={donorId} 
          chatHistory={chatHistory} 
          setChatHistory={setChatHistory} 
          notifications={notifications} 
          setNotifications={setNotifications} 
        />
      ) : (
        <p>No chat history found. Please initiate chat from the donation details page.</p>
      )}
    </div>
  );
};

export default ChatPage;
