import React, { useState, useEffect, createContext } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Login from './components/Login';
import Register from './components/Register';
import DonationForm from './components/DonationForm';
import DonationList from './components/DonationList';
import Navbar from './components/Navbar';
import Home from './components/Home';
import Profile from './components/Profile';
import Notifications from './components/Notifications';
import DonationDetails from './components/DonationDetails';
import ChatPage from './components/ChatPage';
import ChatListPage from './components/ChatListPage'; 
import io from 'socket.io-client'

export const SocketContext = createContext();

const App = () => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [donationId, setDonationId] = useState(null);


  const socket = user ? io('http://localhost:5000', { query: { userId: user.id } }) : null;


  useEffect(() => {

    if (!socket) return;  // Ensure socket is initialized only if user is authenticated


    socket.on('connect', () => {
      console.log("Socket connected with ID:", socket.id);
    });

    socket.on('disconnect', () => {
      console.log("Socket disconnected.");
    });

    socket.on('messageReceived', (messageData) => {
      // Add a new notification when a message is received
      setNotifications((prevNotifications) => [
        ...prevNotifications,
        { sender: messageData.sender, content: messageData.content },
      ]);
    });



    // Clean up socket connection when component unmounts
    return () => {
      socket.disconnect();
      socket.off('messageReceived');
    };
  }, [user]);

  const handleLogin = (userData) => {
    console.log('User logged in with data:', userData);
    setUser(userData);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setUser(null);
    setIsAuthenticated(false);
  };



  return (
    <SocketContext.Provider value={socket}>
    <Router>
      <Navbar
        isAuthenticated={isAuthenticated}
        userType={user?.userType}
        onLogout={handleLogout}
        donorId={user?.id}
      />
      <ToastContainer />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/home" element={<Home />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/register" element={<Register />} />

        {/* Conditional Routes */}
        <Route
          path="/donate"
          element={
            isAuthenticated && user?.userType === 'donor' ? <DonationForm /> : <Navigate to="/login" />
          }
        />
        <Route
          path="/donations"
          element={
            isAuthenticated && user?.userType === 'donee' ? <DonationList /> : <Navigate to="/login" />
          }
        />
        <Route
          path="/profile"
          element={isAuthenticated ? <Profile user={user} /> : <Navigate to="/login" />}
        />
        <Route
          path="/notifications"
          element={
            isAuthenticated ? <Notifications notifications={notifications} /> : <Navigate to="/login" />
          }
        />
        <Route
          path="/donation/:donationId"
          element={
            isAuthenticated ? (
              <DonationDetails currentUserId={user?.id} setDonationId={setDonationId} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/chats" // Route to ChatListPage
          element={
            isAuthenticated ? (
              <ChatListPage currentUserId={user?.id} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/chat/:donorId/:donationId" // Ensure this path is correctly defined
          element={
            isAuthenticated ? (
              <ChatPage
                currentUserId={user?.id}
                notifications={notifications}
                setNotifications={setNotifications}
              />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
        path="/chat/:donorId"  // Chat route for donors
  element={
    isAuthenticated ? (
      <ChatPage
        currentUserId={user?.id}
        notifications={notifications}
        setNotifications={setNotifications}
      />
    ) : (
      <Navigate to="/login" />
    )
  }
/>
      </Routes>
    </Router>
    </SocketContext.Provider>
  );
};

export default App;
