// src/components/Login.js

import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Navigate } from 'react-router-dom';

const Login = ({ setUser }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [redirect, setRedirect] = useState(false); // State to handle redirection
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', { email, password });
      const userData = response.data; // Assuming your backend sends user data
      setUser(userData);
      setRedirect(true); // Set redirect to true on successful login
    } catch (err) {
      // Check if response is defined and has data, else provide a generic error message
      const errorMessage = err.response?.data?.msg || 'An error occurred. Please try again.';
      setError(errorMessage); // Show error message
    }
  };
  // Redirect to /home if login is successful
  if (redirect) {
    return <Navigate to="/home" replace />; // Redirect to homepage
  }

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          placeholder="Email" 
          required 
        />
        <input 
          type="password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          placeholder="Password" 
          required 
        />
        <button type="submit">Login</button>
      </form>
      {error && <p>{error}</p>}
    </div>
  );
};

export default Login;
