// Chat.js
import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import './Chat.css';

const Chat = ({ userId, donorId, donationId, chatHistory, setChatHistory }) => {
  const [newMessage, setNewMessage] = useState("");
  let socket;

  useEffect(() => {
    socket = io('http://localhost:5000');
    
    socket.on('connect', () => {
      console.log('Socket connected');
      socket.emit('joinChat', { donorId, doneeId: userId });
    })

    socket.on('disconnect', () => {
      console.log('Socket disconnected');
    });

    socket.on('messageReceived', message => {
      setChatHistory(prev => [...prev, message]);
    });

    return () => {
      socket.disconnect();
    };
  }, [donorId, userId, setChatHistory]);

  const handleSendMessage = async () => {
    const messageData = {
      sender: userId,
      receiver: donorId,
      message: newMessage,
      donationId: donationId // Include donationId here
    };
    
    console.log("Message Data to Send:", messageData); 
    try {
      const response = await axios.post(`http://localhost:5000/api/chats`, messageData);
      if (response.status === 201) {
        // Emit the message through the socket only if the database save was successful
        socket.emit('sendMessage', messageData);
        setNewMessage(''); // Clear the input field after sending
      }
    } catch (error) {
      console.error("Failed to send message:", error);
    }
  };

  return (
    <div className="chat-container">
      <h3>Chat with Donor</h3>
      <div className="chat-messages">
        {chatHistory && chatHistory.length > 0 ? (
          chatHistory.map((msg, index) => (
            <div
              key={index}
              className={`message-bubble ${msg.sender === userId ? 'sent' : 'received'}`}
            >
              <span className="message-content">{msg.message}</span>
            </div>
          ))
        ) : (
          <p>No messages yet</p>
        )}
      </div>
      <textarea
        value={newMessage}
        onChange={e => setNewMessage(e.target.value)}
        placeholder="Type a message"
      />
      <button onClick={handleSendMessage}>Send</button>
    </div>
  );
};

export default Chat;
