import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import { apiPost } from '../utils/api'; // Import the API utility
import { toast } from 'react-toastify';
import './Chat.css';

const Chat = ({ userId, donorId, donationId, chatHistory = [], setChatHistory }) => { // Default chatHistory to an empty array
  const [newMessage, setNewMessage] = useState("");
  const [error, setError] = useState("");
  const socket = io('http://localhost:5000');

  useEffect(() => {
    socket.on('connect', () => {
      console.log('Socket connected');
      socket.emit('joinChat', { donorId, doneeId: userId });
    });

    socket.on('disconnect', () => {
      console.log('Socket disconnected');
    });

    socket.on('messageReceived', (message) => {
      setChatHistory((prev) => [...prev, message]);
      toast(`New message from ${message.sender}: ${message.message}`);
    });

    return () => {
      socket.off('messageReceived');
      socket.disconnect();
    };
  }, [donorId, userId, setChatHistory, socket]);

  const handleSendMessage = async () => {
    if (!newMessage.trim()) return;

    const messageData = {
      sender: userId,
      receiver: donorId,
      message: newMessage,
      donationId: donationId,
    };

    try {
      const response = await apiPost('/api/chats', messageData);
      if (response) {
        socket.emit('sendMessage', messageData);
        setNewMessage('');
        setError('');
        toast('Message sent!');
      }
    } catch (error) {
      setError(error.message); // Display user-friendly error message
      console.error("Failed to send message:", error);
    }
  };

  return (
    <div className="chat-container">
      <h3>Chat with Donor</h3>
      {error && <p className="error-message">{error}</p>} {/* Display error message if exists */}
      <div className="chat-messages">
        {chatHistory.length > 0 ? (
          chatHistory.map((msg, index) => (
            <div key={index} className={`message-bubble ${msg.sender === userId ? 'sent' : 'received'}`}>
              <span className="message-content">{msg.message}</span>
            </div>
          ))
        ) : (
          <p>No messages yet</p>
        )}
      </div>
      <textarea
        value={newMessage}
        onChange={(e) => setNewMessage(e.target.value)}
        placeholder="Type a message"
      />
      <button onClick={handleSendMessage} disabled={!newMessage.trim()}>Send</button>
    </div>
  );
};

export default Chat;
