// src/components/DonationDetails.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const DonationDetails = ({ donation, currentUserId }) => {
  const [initialMessage, setInitialMessage] = useState("");
  const navigate = useNavigate();

  const handleSendMessage = async () => {
    if (!initialMessage.trim()) return; // Prevent empty messages

    try {
      // Sending initial message to create a chat in the database
      await axios.post('/api/chats', {
        sender: currentUserId, // donee ID
        receiver: donation.donorId, // donor ID from donation data
        message: initialMessage,
      });

      // Clear the text box after sending
      setInitialMessage("");

      // Redirect to chat page after sending the first message
      navigate(`/chat/${donation.donorId}`);
    } catch (error) {
      console.error("Error initiating chat:", error);
    }
  };

  return (
    <div>
      <h2>{donation.itemName}</h2>
      {/* Other donation details */}
      <p>{donation.description}</p>
      <p>Category: {donation.category}</p>
      <p>Location: {donation.location}</p>
      <p>Status: {donation.status}</p>

      {/* Text box and Send Message button */}
      <textarea
        placeholder="Type your message..."
        value={initialMessage}
        onChange={(e) => setInitialMessage(e.target.value)}
      />
      <button onClick={handleSendMessage}>Send Message</button>
    </div>
  );
};

export default DonationDetails;
