import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { apiGet, apiPost } from '../utils/api';
import './ChatPage.css';
import { SocketContext } from '../App';



const ChatPage = ({ currentUserId }) => {
  const { donorId, donationId } = useParams();
  const [chatGroups, setChatGroups] = useState([]);
  const [selectedChatGroupId, setSelectedChatGroupId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const navigate = useNavigate();
  const socket = useContext(SocketContext);

  useEffect(() => {
    // Fetch chat groups for the current user
    const fetchChatGroups = async () => {
      try {
        const response = await apiGet(`/api/chats/user/${currentUserId}`);
        setChatGroups(response); // Ensure response data matches the frontend's expectations
      } catch (error) {
        console.error("Error fetching chat groups:", error);
      }
    };

    fetchChatGroups();
  }, [currentUserId]);

  // Fetch messages for the selected chat group
  useEffect(() => {
    // Check if `selectedChatGroupId` is set and add a socket listener
    if (selectedChatGroupId) {
      const fetchMessages = async () => {
        try {
          const response = await apiGet(`/api/chats/messages/${selectedChatGroupId}`);
          setMessages(response);
        } catch (error) {
          console.error("Error fetching messages:", error);
        }
      };
      fetchMessages();
  
      // Listen for new messages on socket only for the selected chat group
      socket.on('messageReceived', (messageData) => {
        console.log("Received message:", messageData);
        if (messageData.chatGroupId === selectedChatGroupId) {
          setMessages((prevMessages) => [...prevMessages, messageData]);
        }
      });
  
      // Cleanup listener to avoid duplicate listeners
      return () => {
        socket.off('messageReceived');
      };
    }
  }, [selectedChatGroupId, socket]); // Runs only when selectedChatGroupId changes

  const handleSendMessage = async () => {
    if (!newMessage.trim()) return;

    try {
      const response = await apiPost('/api/messages', {
        chatGroupId: selectedChatGroupId,
        senderId: currentUserId,
        content: newMessage,
      });

      const newMsgData = {
        chatGroupId: selectedChatGroupId,
        sender: currentUserId,
        content: newMessage,
        _id: response.messageId,
      };

      setNewMessage('');
      setMessages((prevMessages) => [
        ...prevMessages,
        {
          sender: currentUserId, 
          content: newMessage, 
          _id: response.messageId, // Assuming response includes messageId
        },
      ]);
      socket.emit('sendMessage', newMsgData);
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  return (
    <div className="chat-page">
      <div className="chat-list">
        <h2>Chats</h2>
        {chatGroups.length > 0 ? (
          chatGroups.map((chatGroup) => (
            <div
              key={chatGroup._id}
              onClick={() => setSelectedChatGroupId(chatGroup._id)}
              className={selectedChatGroupId === chatGroup._id ? 'chat-item active' : 'chat-item'}
            >
              {chatGroup.groupName} {/* Assuming groupName exists */}
            </div>
          ))
        ) : (
          <p>No chats available.</p>
        )}
      </div>
      <div className="chat-messages">
        {selectedChatGroupId ? (
          <>
            <div className="messages">
              {messages.map((msg) => (
                <div
                  key={msg._id} // Ensure each message has a unique _id
                  className={`message ${msg.sender === currentUserId ? 'own' : ''}`}
                >
                  {msg.content}
                </div>
              ))}
            </div>
            <div className="message-input">
              <input
                type="text"
                placeholder="Type a message..."
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
              />
              <button onClick={handleSendMessage}>Send</button>
            </div>
          </>
        ) : (
          <p>Select a chat to start messaging</p>
        )}
      </div>
    </div>
  );
};

export default ChatPage;
