import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify'; // Import ToastContainer
import 'react-toastify/dist/ReactToastify.css'; // Import toast styles
import Login from './components/Login';
import Register from './components/Register';
import DonationForm from './components/DonationForm';
import DonationList from './components/DonationList';
import Navbar from './components/Navbar';
import Home from './components/Home';
import Profile from './components/Profile';
import Notifications from './components/Notifications';
import DonationDetails from './components/DonationDetails';
import Chat from './components/Chat';
import ChatPage from './components/ChatPage';

const App = () => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [selectedDoneeId, setSelectedDoneeId] = useState(null);
  const [donationId, setDonationId] = useState(null); // State to hold the current donationId

  const handleLogin = (userData) => {
    console.log('User logged in with data:', userData); // Debugging log
    setUser(userData); // userData now includes the ID
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setUser(null);
    setIsAuthenticated(false);
  };

  return (
    <Router>
      <Navbar 
        isAuthenticated={isAuthenticated} 
        userType={user?.userType} 
        onLogout={handleLogout} 
        donorId={user?.id} 
        donationId={donationId} // Pass the current donationId
      />
      <ToastContainer /> {/* Add ToastContainer here */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/home" element={<Home />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/register" element={<Register />} />

        {/* Conditional Routes */}
        <Route 
          path="/donate" 
          element={isAuthenticated && user?.userType === 'donor' ? <DonationForm /> : <Navigate to="/login" />} 
        />
        <Route 
          path="/donations" 
          element={isAuthenticated && user?.userType === 'donee' ? <DonationList /> : <Navigate to="/login" />} 
        />
        <Route 
          path="/profile" 
          element={isAuthenticated ? <Profile user={user} /> : <Navigate to="/login" />} 
        />
        <Route 
          path="/notifications" 
          element={isAuthenticated ? <Notifications notifications={notifications} /> : <Navigate to="/login" />} 
        />
        <Route 
          path="/donation/:donationId" 
          element={isAuthenticated ? <DonationDetails currentUserId={user?.id} setDonationId={setDonationId} /> : <Navigate to="/login" />} 
        />
        <Route
          path="/chat/:donorId"
          element={
            isAuthenticated ? (
              <ChatPage 
                currentUserId={user?.id} 
                notifications={notifications} 
                setNotifications={setNotifications} 
              />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/chat"
          element={
            isAuthenticated ? (
              <Chat 
                donorId={user?.id} 
                doneeId={selectedDoneeId} 
                notifications={notifications} 
                setNotifications={setNotifications}
              />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
      </Routes>
    </Router>
  );
};

export default App;
