import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';

const Chat = ({ userId }) => {
  const { donorId } = useParams();  // Access donorId from URL
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  let socket;

  useEffect(() => {
    // Connect to WebSocket server
    socket = io('http://localhost:5000');
    socket.emit('joinChat', { donorId, doneeId: userId });

      // Listen for incoming messages
      socket.on('messageReceived', message => {
        setMessages(prev => [...prev, message]);
      });

    // Fetch previous messages
    console.log(`Fetching messages from: /api/chats/${donorId}/${userId}`);
    fetch(`/api/chats/${donorId}/${userId}`)
      .then(res => {
        if (!res.ok) {
          throw new Error('Network response was not ok');
        }
        return res.json();
      })
      .then(data => setMessages(data))
      .catch(err => {
        console.error(err);
        alert('Failed to fetch messages. Please try again later.');
      });

    socket.on('messageReceived', message => {
      setMessages(prev => [...prev, message]);
    });

    return () => {
      socket.disconnect();
    };
  }, [donorId, userId]);

  const handleSendMessage = () => {
    const messageData = { sender: userId, receiver: donorId, message: newMessage };
    socket.emit('sendMessage', messageData);
    setNewMessage('');
  };

  return (
    <div>
      <h3>Chat with Donor</h3>
      <div>
        {messages.map((msg, index) => (
          <p key={index}>{msg.sender === userId ? "You" : "Donor"}: {msg.message}</p>
        ))}
      </div>
      <textarea
        value={newMessage}
        onChange={e => setNewMessage(e.target.value)}
        placeholder="Type a message"
      />
      <button onClick={handleSendMessage}>Send</button>
    </div>
  );
};

export default Chat;
