// Chat.js
import { useEffect, useState } from 'react';
import io from 'socket.io-client';
import './Chat.css';

const socket = io('http://localhost:5000'); // Adjust this to your backend URL

const Chat = ({ donorId, doneeId, notifications, setNotifications }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  useEffect(() => {
    // Listen for messages received from the server
    socket.on('messageReceived', (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
      
      // Add to notifications if message is relevant to the current user
      if (message.receiver === donorId || message.sender === donorId) {
        setNotifications((prevNotifications) => [...prevNotifications, message]);
      }
    });

    return () => {
      socket.off('messageReceived');
    };
  }, [donorId, setNotifications]);

  const handleSendMessage = () => {
    if (!newMessage) return;

    socket.emit('sendMessage', {
      sender: donorId,
      receiver: doneeId,
      message: newMessage,
    });
    setNewMessage('');
  };

  return (
    <div className="chat-container">
      <h3>Chat</h3>
      <div style={{ maxHeight: '300px', overflowY: 'scroll' }}>
        {messages.map((msg) => (
          <div key={msg._id} className="chat-message">
            <strong>{msg.sender === donorId ? 'Donor' : 'Donee'}:</strong> {msg.message}
          </div>
        ))}
      </div>
      <textarea
        className="chat-input"
        value={newMessage}
        onChange={(e) => setNewMessage(e.target.value)}
        placeholder="Type your message..."
      />
      <button onClick={handleSendMessage}>Send</button>
    </div>
  );
};

export default Chat;
