import React, { useEffect, useState } from 'react'; 
import { useParams } from 'react-router-dom';
import { apiGet } from '../utils/api';
import Chat from './Chat';
import './Chat.css';

const ChatPage = ({ currentUserId }) => {
  let abc = useParams();
  console.log({abc});
  
  const { donorId, donationId } = useParams();
  const [error, setError] = useState(null);
  const [isChatInitiated, setIsChatInitiated] = useState(false);
  const [itemName, setItemName] = useState('');

  useEffect(() => {
    const checkIfChatExists = async () => {
      try {
        const response = await apiGet(`/api/chats/existence/${currentUserId}/${donorId}/${donationId}`);
        setIsChatInitiated(response?.exists);

        const donationResponse = await apiGet(`/api/donations/${donationId}`);
        setItemName(donationResponse?.itemName || "Item Name Not Found");
      } catch (err) {
        console.error("Error fetching chat data:", err);
        setError("Unable to load chat data. Please try again later.");
      }
    };

    checkIfChatExists();
  }, [currentUserId, donorId, donationId]);

  return (
    <div className="chat-page">
      <h2>Chat about: {itemName}</h2>
      {error && <p className="error-message">{error}</p>}
      {isChatInitiated ? (
        <Chat 
          userId={currentUserId} 
          donorId={donorId} 
          donationId={donationId} 
        />
      ) : (
        <p>No chat initiated. Please start a chat from the donation details page.</p>
      )}
    </div>
  );
};

export default ChatPage;
