// ChatPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Chat from './Chat';

const ChatPage = ({ currentUserId, notifications, setNotifications }) => {
  const { donorId } = useParams();
  const [chatHistory, setChatHistory] = useState([]); // Initialize as an empty array
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchChatHistory = async () => {
      try {
        const response = await axios.get(`/api/chats/${currentUserId}/${donorId}`);
        setChatHistory(response.data || []); // Default to an empty array if no data
      } catch (err) {
        setError('Error fetching chat history');
      }
    };

    fetchChatHistory();
  }, [donorId, currentUserId]);

  if (error) return <p>{error}</p>;

  return (
    <div className="chat-page">
      <h2>Chat with Donor</h2>
      <Chat 
        donorId={donorId} 
        userId={currentUserId} 
        chatHistory={chatHistory} 
        setChatHistory={setChatHistory} 
        notifications={notifications} 
        setNotifications={setNotifications} 
      />
    </div>
  );
};

export default ChatPage;
