// src/components/DonationList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const DonationList = () => {
  const [donations, setDonations] = useState([]);
  const [error, setError] = useState(null); // State to hold any error messages
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDonations = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/donations', {
          headers: {
            Authorization: `Bearer ${token}`, // If your endpoint requires authentication
          },
        });
        setDonations(response.data);
      } catch (error) {
        console.error('Error fetching donations:', error);
        setError('Failed to fetch donations.'); // Set error message
      }
    };

    fetchDonations();
  }, []);

  const handleDonationClick = (donationId) => {
    navigate(`/donation/${donationId}`);
  };

  return (
    <div>
      <h2>Available Donations</h2>
      {error && <p className="error">{error}</p>} {/* Display error if any */}
      <ul>
        {donations.map((donation) => (
          <li key={donation._id}>
            <Link to={`/donation/${donation._id}`}>
              {donation.itemName}
            </Link>
            {' - '}
            <span>{donation.location}</span>
            <button onClick={() => handleDonationClick(donation._id)}>
              View Details
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DonationList;
